// Funci√≥n para obtener palabras filtradas por categor√≠as y dificultad
export function getWordsFromCategories(
  categoryIds: string[], 
  difficulty?: Difficulty
): WordWithDifficulty[] {
  let words: WordWithDifficulty[];
  
  if (categoryIds.length === 0) {
    words = categories.flatMap(cat => cat.words);
  } else {
    words = categories
      .filter(cat => categoryIds.includes(cat.id))
      .flatMap(cat => cat.words);
  }
  
  if (difficulty) {
    // Filtrar por dificultad
    const difficultyOrder: Difficulty[] = ["facil", "normal", "dificil", "leyenda"];
    const maxDifficultyIndex = difficultyOrder.indexOf(difficulty);
    
    words = words.filter(word => {
      const wordDiffIndex = difficultyOrder.indexOf(word.difficulty);
      return wordDiffIndex <= maxDifficultyIndex;
    });
  }
  
  return words;
}

// Funci√≥n para obtener una palabra aleatoria con pista seg√∫n dificultad
export function getRandomWord(
  categoryIds: string[] = [], 
  difficulty: Difficulty = "normal"
): { word: string; hint: string; wordDifficulty: Difficulty } {
  const words = getWordsFromCategories(categoryIds, difficulty);
  
  if (words.length === 0) {
    return { word: "Error", hint: "No hay palabras disponibles", wordDifficulty: "facil" };
  }
  
  const randomIndex = Math.floor(Math.random() * words.length);
  const selectedWord = words[randomIndex];
  
  // La pista se selecciona seg√∫n la dificultad elegida
  const hint = selectedWord.hints[difficulty];
  
  return {
    word: selectedWord.word,
    hint: hint,
    wordDifficulty: selectedWord.difficulty
  };
}

// Exportar la cantidad total de palabras
export const totalWords = categories.reduce((acc, cat) => acc + cat.words.length, 0);

// Obtener cantidad de palabras filtradas por dificultad para una categor√≠a espec√≠fica
export function getWordCountForCategory(categoryId: string, difficulty?: Difficulty): number {
  const category = categories.find(cat => cat.id === categoryId);
  if (!category) return 0;
  
  if (!difficulty) return category.words.length;
  
  const difficultyOrder: Difficulty[] = ["facil", "normal", "dificil", "leyenda"];
  const maxDifficultyIndex = difficultyOrder.indexOf(difficulty);
  
  return category.words.filter(word => {
    const wordDiffIndex = difficultyOrder.indexOf(word.difficulty);
    return wordDiffIndex <= maxDifficultyIndex;
  }).length;
}

// Contar palabras por dificultad
export function countWordsByDifficulty(): Record<Difficulty, number> {
  const counts: Record<Difficulty, number> = {
    facil: 0,
    normal: 0,
    dificil: 0,
    leyenda: 0
  };
  
  categories.forEach(cat => {
    cat.words.forEach(word => {
      counts[word.difficulty]++;
    });
  });
  
  return counts;
}
